     */
    function get_category($idCategories)
    {
        return $this->db->get_where('Categories',array('idCategories'=>$idCategories))->row_array();
    }
    
    /*
     * Get all categories count
     */
    function get_all_categories_count()
    {
        $this->db->from('Categories');
        return $this->db->count_all_results();
    }
        
    /*
     * Get all categories
     */
    function get_all_categories($params = array())
    {
        $this->db->order_by('idCategories', 'desc');
