        $this->db->insert('Categories',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update category
     */
    function update_category($idCategories,$params)
    {
        $this->db->where('idCategories',$idCategories);
        return $this->db->update('Categories',$params);
    }
    
    /*
     * function to delete category
     */
    function delete_category($idCategories)
    {
        return $this->db->delete('Categories',array('idCategories'=>$idCategories));
